<?php if ( !defined("root" ) ) die; ?><div class="a_row"><?php foreach( $loader->genre->get_all() as $genre ) : if ( empty( $genre["image"] ) ) continue; ?><a href="<?php $loader->ui->eurl( "genre", $genre["url"] ); ?>" class="slider_item genre<?php echo $genre["image"] ? " has_image" : ""; ?>" <?php if ( !empty( $genre["image"] ) ) echo "style='background-image:url({$genre["image_addr"]})'"; ?>><span class="text"><?php echo $loader->secure->escape( $genre["name"] ); ?></span></a><?php endforeach; ?></div>
